import 'package:flutter/material.dart';

void main() => runApp(MyApp());

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      debugShowCheckedModeBanner: false,
      home: WalkthroughScreen(),
    );
  }
}

class WalkthroughScreen extends StatelessWidget {
  final List<WalkthroughPageModel> pages = [
    WalkthroughPageModel(
      imageUrl: 'data:image/jpeg;base64,', // Replace with your network image URL
      title: 'View and buy\nMedicine online',
      description: 'Etiam mollis metus non purus faucibus sollicitudin.',
    ),
    WalkthroughPageModel(
      imageUrl: 'data:image/jpeg;base64,', // Replace with your network image URL
      title: 'Fast Delivery\nOptions',
      description: 'Get your products delivered quickly and safely.',
    ),
    WalkthroughPageModel(
      imageUrl: 'data:image/jpeg;base64,', // Replace with your network image URL
      title: 'Track Your Orders',
      description: 'Stay updated with real-time order tracking.',
    ),
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: PageView.builder(
        itemCount: pages.length,
        itemBuilder: (context, index) {
          final page = pages[index];
          return WalkthroughPage(
            imageUrl: page.imageUrl,
            title: page.title,
            description: page.description,
            isLastPage: index == pages.length - 1,
            onNextPressed: () {
              if (index == pages.length - 1) {
                // Navigate to the next screen or main app
                print("Walkthrough Completed!");
              }
            },
          );
        },
      ),
    );
  }
}

class WalkthroughPage extends StatelessWidget {
  final String imageUrl;
  final String title;
  final String description;
  final bool isLastPage;
  final VoidCallback onNextPressed;

  WalkthroughPage({
    required this.imageUrl,
    required this.title,
    required this.description,
    required this.isLastPage,
    required this.onNextPressed,
  });

  @override
  Widget build(BuildContext context) {
    return Padding(
      padding: const EdgeInsets.all(16.0),
      child: Column(
        mainAxisAlignment: MainAxisAlignment.center,
        children: [
          Image.network(
            imageUrl,
            height: 300,
            errorBuilder: (context, error, stackTrace) {
              return Icon(Icons.error, size: 100, color: Colors.red);
            },
          ),
          SizedBox(height: 24),
          Text(
            title,
            textAlign: TextAlign.center,
            style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold),
          ),
          SizedBox(height: 16),
          Text(
            description,
            textAlign: TextAlign.center,
            style: TextStyle(fontSize: 16, color: Colors.grey),
          ),
          Spacer(),
          Row(
            mainAxisAlignment: MainAxisAlignment.spaceBetween,
            children: [
              TextButton(
                onPressed: () {
                  // Handle skip logic here
                  print("Skipped");
                },
                child: Text("Skip"),
              ),
              ElevatedButton(
                onPressed: onNextPressed,
                child: Text(isLastPage ? "Finish" : "Next"),
              ),
            ],
          ),
        ],
      ),
    );
  }
}

class WalkthroughPageModel {
  final String imageUrl;
  final String title;
  final String description;

  WalkthroughPageModel({
    required this.imageUrl,
    required this.title,
    required this.description,
  });
}
